import React, { useState } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import Layout from '../components/Layout';
import { useLocation } from 'react-router-dom';

export default function Login() {
  const location = useLocation();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [userType, setUserType] = useState(location.state && location.state.admin ? 'admin' : 'user');

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/auth/login', { email, password, user_type: userType });
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user_type', res.data.user_type);
      window.location.href = res.data.user_type === 'admin' ? '/admin' : '/user';
    } catch (err) {
      toast.error(err.response?.data?.error || 'Login failed');
    }
  };

  return (
    <Layout>
      <div className="flex items-center justify-center min-h-[60vh]">
        <form className="bg-white p-10 rounded-2xl shadow-xl w-96 flex flex-col gap-4 border border-blue-100" onSubmit={handleLogin}>
          <h2 className="text-3xl font-extrabold mb-2 text-blue-700 text-center tracking-tight">Sign in to Docky</h2>
          <select value={userType} onChange={e => setUserType(e.target.value)} className="mb-2 w-full p-2 border rounded focus:ring-2 focus:ring-blue-200">
            <option value="user">User</option>
          </select>
          {/* Admin login fields (hidden unless userType is 'admin') */}
          {userType === 'user' && (
            <>
              <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} className="w-full p-2 border rounded focus:ring-2 focus:ring-blue-200" required />
              <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} className="w-full p-2 border rounded focus:ring-2 focus:ring-blue-200" required />
            </>
          )}
          {userType === 'admin' && (
            <>
              <input type="email" placeholder="Admin Email" value={email} onChange={e => setEmail(e.target.value)} className="w-full p-2 border rounded focus:ring-2 focus:ring-blue-200" required />
              <input type="password" placeholder="Admin Password" value={password} onChange={e => setPassword(e.target.value)} className="w-full p-2 border rounded focus:ring-2 focus:ring-blue-200" required />
            </>
          )}
          <button type="submit" className="w-full bg-blue-600 hover:bg-blue-700 text-white py-2 rounded font-semibold transition">Login</button>
          <div className="mt-2 text-center">
            <a href="/signup" className="text-blue-500 hover:underline">Sign up</a>
          </div>
        </form>
      </div>
    </Layout>
  );
}
