import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function SetDeadline() {
  const [deadline, setDeadline] = useState('');
  const [current, setCurrent] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    fetchCurrent();
  }, []);

  const fetchCurrent = async () => {
    try {
      const res = await axios.get('/api/settings/deadline', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setCurrent(res.data.deadline_datetime);
    } catch {}
  };

  const handleSet = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    try {
      await axios.post('/api/settings/deadline', { deadline_datetime: deadline }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setSuccess('Deadline updated!');
      fetchCurrent();
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to set deadline');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50">
      <form className="bg-white p-8 rounded shadow w-96" onSubmit={handleSet}>
        <h2 className="text-2xl font-bold mb-4">Set Submission Deadline</h2>
        <div className="mb-2">Current: {current ? new Date(current).toLocaleString() : 'Not set'}</div>
        <input type="datetime-local" value={deadline} onChange={e => setDeadline(e.target.value)} className="mb-4 w-full p-2 border rounded" required />
        {error && <div className="text-red-500 mb-2">{error}</div>}
        {success && <div className="text-green-500 mb-2">{success}</div>}
        <button type="submit" className="w-full bg-blue-500 text-white py-2 rounded">Set Deadline</button>
        <div className="mt-4 text-center">
          <a href="/admin" className="text-blue-500">Back to Dashboard</a>
        </div>
      </form>
    </div>
  );
}
